<nav>    <ul id="nt_menu_id" class="nt_menu">        @foreach (\App\Models\Menu::where('parent_id', 0)->where('status','!=', 1)->orderBy('position', 'asc')->get() as $key => $menu)            <li>                <?php if($menu->type=='category' && $menu->category_id!=0) {                $cat=\App\Models\Category::find($menu->category_id);?>                    <a class="{{url()->current() == route('products.category',  $cat->slug) ? 'active' : '' }}" href="{{ route('products.category',  $cat->slug) }}" target="{{ $menu->target }}">{{ $menu->getTranslation('name') }}</a>                <?php }elseif($menu->type=='brand' && $menu->brand_id!=0) {                    $brand=\App\Models\Brand::find($menu->brand_id);?>                    <a class="{{url()->current() == route('products.brand',  $brand->slug) ? 'active' : '' }}" href="{{ route('products.brand',  $brand->slug) }}" target="{{ $menu->target }}">{{ $menu->getTranslation('name') }}</a>                <?php }elseif($menu->type=='custom'){?>                    <a class="{{url()->current() == $menu->url ? 'active' : '' }}" href="{{ url($menu->url) }}" target="{{ $menu->target }}">{{ $menu->getTranslation('name') }}</a>                <?php }else{                    ?>                    <a href="#">{{ $menu->getTranslation('name') }}</a>                <?php                }?>            </li>        @endforeach    </ul></nav>